package main

import (
	"fmt"
	"math/rand"
	"time"
)

/**
 * @Author: luo
 * @Author: hustly123@gmail.com
 * @Date: 2021/7/2 9:45
 * @Desc: 三数之和，给一个包含n个整数的数组nums，判断nums是否存在三个元素a b c，使得a+b+c=0，找出所有和为0且不重复的三元组
 */

func threeSum(nums []int) [][]int {
	if len(nums) < 3 || nums == nil {
		return [][]int{}
	}
	qsort(nums, 0, len(nums)-1)
	// sort.Ints(nums)
	ret := make([][]int, 0)
	// 加了个nums[i]<=0才能提交
	for i := 0; i < len(nums) && nums[i] <= 0; i++ {
		if i == 0 || nums[i] != nums[i-1] { // 跳过重复
			for j := i + 1; j < len(nums); j++ {
				if j == i+1 || nums[j] != nums[j-1] {
					k := len(nums) - 1
					for k > j && nums[i]+nums[j]+nums[k] > 0 {
						k--
					}

					// 这里是官方题解和我的题解不同的地方
					if k == j {
						break
					}

					if nums[i]+nums[j]+nums[k] == 0 {
						ret = append(ret, []int{nums[i], nums[j], nums[k]})
					}
				}
			}
		}
	}
	return ret
}

// 练手 手写快速排序 好久不写 手生了
func qsort(nums []int, l, r int) {
	if l < r {
		pivot := partition(nums, l, r)
		qsort(nums, l, pivot-1)
		qsort(nums, pivot+1, r)
	}
}

func partition(nums []int, l, r int) int {
	rand.Seed(time.Now().Unix())
	var (
		pos, k, i int
	)
	pos = l + rand.Intn(r-l+1)
	nums[pos], nums[r] = nums[r], nums[pos]
	k = l - 1
	for i = l; i < r; i++ {
		if nums[i] < nums[r] {
			nums[k+1], nums[i] = nums[i], nums[k+1]
			k++
		}
	}
	nums[k+1], nums[r] = nums[r], nums[k+1]
	return k + 1
}

